import { Link } from "react-router-dom";

const Country = ({data}) => {
    console.log(data);
    return (
        <>
            <div className='home-button-container'>
                <Link to='/'>
                    <i className="fas fa-arrow-left"></i>
                    Home
                </Link>
            </div>
            <div className='country'>
                <div className='country__flag'>
                    <img src={data.flag} className='country__flag__pic' alt=''/>
                </div>
                <div className='country__info'>
                    <h3 className='country__title'></h3>
                    <div className='country__data'>
                        <ul className='country__stats'>
                            <li className='country__stat'>
                                <span>Native Name</span>{data.nativeName}
                            </li>
                            <li className='country__stat'>
                                <span>Population</span>{data.population}
                            </li>
                            <li className='country__stat'>
                                <span>Region</span>{data.region}
                            </li>
                            <li className='country__stat'>
                                <span>Sub Region</span>{data.subregion}
                            </li>
                            <li className='country__stat'>
                                <span>Capital</span>{data.capital}
                            </li>
                        </ul>
                        <ul className='country__stats'>
                            <li className='country__stat'>
                                <span>Top Level Domain</span>
                                {
                                    data.topLevelDomain
                                }
                            </li>
                            <li className='country__stat'>
                                <span>Currencies</span>
                                {
                                    data.currencies.map(currency => {
                                        return currency.name
                                    })
                                }
                            </li>
                            <li className='country__stat'>
                                <span>Languages</span>
                                {
                                    data.languages.map(language => {
                                        return language.name
                                    })
                                }
                            </li>
                        </ul>
                    </div>
                    <div className='country__border'>
                        <h3>Border Countries</h3>
                        <ul className='country__border_list'>
                            {
                                data.borders.map(border => {
                                    return <li className='country__border__child' key={border}>{border}</li>
                                })
                            }
                        </ul>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Country;
