import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Country = ({data}) => {

    const [borderContent, setBorderContent] = useState([]);
    const [ErrorOfData, setErrorOfData] = useState(false);

    useEffect(() => {
        console.log(borderContent);
    }, [borderContent])

    const getBorderFromAPI = async (arg) => {
        let url = `https://restcountries.eu/rest/v2/name/${arg}`;
        fetch(url)
          .then(resp => {
              if(resp.status === 404) {
                return ''
              } else {
                  return resp.json();
              }
          })
          .then(data => {
            if(data) {
                data.map(({alpha3Code, name }) => {
                    if(alpha3Code === arg) {
                        var container = borderContent;
                        container.push(name);
                        setBorderContent(container);
                        console.log(borderContent);
                        setTimeout(() => {
                           console.log('test'); 
                        }, 3000);
                    }
                })
            }
          })
          .catch(err => {
              console.log(`Error with ${arg}`);
          })
    }

    return (
        <>
            <div className='home-button-container'>
                <Link to='/'>
                    <i className="fas fa-arrow-left"></i>
                    Home
                </Link>
            </div>
            <div className='country'>
                <div className='country__flag'>
                    <img src={data.flag} className='country__flag__pic' alt=''/>
                </div>
                <div className='country__info'>
                    <h3 className='country__title'></h3>
                    <div className='country__data'>
                        <ul className='country__stats'>
                            <li className='country__stat'>
                                <span>Native Name</span>{data.nativeName}
                            </li>
                            <li className='country__stat'>
                                <span>Population</span>{data.population}
                            </li>
                            <li className='country__stat'>
                                <span>Region</span>{data.region}
                            </li>
                            <li className='country__stat'>
                                <span>Sub Region</span>{data.subregion}
                            </li>
                            <li className='country__stat'>
                                <span>Capital</span>{data.capital}
                            </li>
                        </ul>
                        <ul className='country__stats'>
                            <li className='country__stat'>
                                <span>Top Level Domain</span>
                                {
                                    data.topLevelDomain
                                }
                            </li>
                            <li className='country__stat'>
                                <span>
                                    {
                                        data.currencies.length > 1 ? 'Currencies' : 'Currency'
                                    }
                                </span>
                                {
                                    data.currencies.map(currency => {
                                        return currency.name
                                    })
                                }
                            </li>
                            <li className='country__stat'>
                                <span>
                                    {
                                        data.languages.length > 1 ? 'Languages' : 'Language'
                                    }
                                </span>
                                {
                                    data.languages.map(language => {
                                        return language.name
                                    })
                                }
                            </li>
                        </ul>
                    </div>
                    {
                        data.borders.map(border => {
                            getBorderFromAPI(border);
                        })
                    }
                    {
                        borderContent ? (
                            <div className='country__border'>
                                <h3>Border Countries</h3>
                                    <ul className='country__border_list'>
                                        {
                                            borderContent.map(data => {
                                                return 'test';                                                
                                            })
                                        }
                                    </ul>
                            </div>
                        ) : ErrorOfData ? (
                            <p>Error</p>
                        ) : (
                            <>
                                borders are loading
                            </>
                        )
                    }
                </div>
            </div>
        </>
    )
}

export default Country;
