import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import NoMatch from './NoMatch';

const Country = () => {
    
    const countryName = useParams('id');
    const url = `https://restcountries.eu/rest/v2/name/${countryName.id}?fullText=true`;
    const [countryData, setCountryData ] = useState(false);

    const fetchdata = async (url) => {
        fetch(url)
          .then(resp => {
            if(http_response_ok(resp.status)) {
                return resp.json();
            } else {
                console.log(`error ${resp.status}`);
            }
          })
          .then(data => {
            console.log(data);
          })
          .catch(err => {
              console.log(err);
          })
    }

    useEffect(() => {
        fetchdata(url);
    }, [url])

    return (
        <section>
            {typeof countryData === 'object' ? (
                console.log(countryData)
            ) : (
                <p>Bye</p>
            )
            }
        </section>
    )
}

export default Country;
