import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import NoMatch from './NoMatch';

const Country = () => {
    
    const countryName = useParams('id');
    const url = `https://restcountries.eu/rest/v2/name/${countryName.id}?fullText=true`;
    const [countryData, setCountryData ] = useState(null);

    const fetchdata = async (url) => {
        try {
            const res = await fetch(url);
            const data = res.json();
            setCountryData(data);
        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        fetchdata(url);
    }, [url])

    return (
        <section>
            {
                countryData ? (
                    countryData.map((country, i) => {
                        let langArr = country.languages.map(lang => lang.name);
                        langArr = langArr.join(', ');

                        return (
                            <>
                                <div className='backToHome'>
                                    <button className='backtohome__button'>
                                        <i className="fas fa-arrow-circle-left"></i>
                                        Home
                                    </button>
                                </div>
                                <div className='contryContainer'>
                                    <div className='countryContainer__child'></div>
                                    <div className='countryContainer__child'></div>
                                </div>
                            </>
                        )
                    })
                ) : (
                    <NoMatch/>
                )
            }
        </section>
    )
}

export default Country;
